INSERT INTO `x2_questions` VALUES ('616', '根据会计法律制度的规定，下列各项中，没有违背会计人员回避制度规定的是（）。', 'DXT', '甲单位法定代表人的女儿担任本单位财务部门科长\n<hr>乙单位财务经理的妻子担任本单位出纳员\n<hr>丙单位法定代表人的高中同学担任本单位财务部门科长\n<hr>丁单位财务科长的女婿担任本单位出纳员', '4', 'C', '', '3', '6', '1', '0', '0', '1', '', '1548511955');
INSERT INTO `x2_questions` VALUES ('617', '根据行政诉讼法律制度的规定，对海关处理的案件提起的诉讼，第一审行政案件由（）管辖。', 'DXT', '基层人民法院\n<hr>中级人民法院\n<hr>高级人民法院\n<hr>最高人民法院', '4', 'B', '', '3', '6', '1', '0', '0', '1', '', '1548511955');
